import React from "react";

export const Testimonials = (props) => {
  return (
    <div
      id="testimonials"
      style={{
        backgroundImage: 'url("img/Research.jpg")', 
        backgroundSize: 'cover',                     
        backgroundPosition: 'center',               
        backgroundRepeat: 'no-repeat',
        padding: '100px 0',                          // Adjust padding for visual spacing
        color: '#fff'                                // Set text color to white for contrast (optional)
      }}
    >
      <div className="container">
        <div className="section-title text-center">
          <h2>RESEARCH</h2> {/* Section title */}
        </div>
        {/* Optional: You can add additional content or text here if needed */}
      </div>
    </div>
  );
};
